import React from "react";
import { FaLinkedin, FaTwitter } from "react-icons/fa";

const Leadership = () => {
  const leaders = [
    {
      
      image: "./components/llogo.png",
      
    },
    {
      image: "/images/leader2.jpg",
      
    },
    {
      image: "/images/leader3.jpg",
      
    },
    {
      image: "/images/leader4.jpg",
     
    },
    {
      name: "Michael Brown",
      image: "/images/leader5.jpg",
      
    },
  ];

  return (
    <div
      style={{
        padding: "50px",
        maxWidth: "1200px",
        margin: "0 auto",
        textAlign: "center",
      }}
    >
      {/* Heading */}
      <h1 style={{ fontSize: "36px", fontWeight: "bold", color: "#333" }}>
        Our Leadership
      </h1>
      <p style={{ fontSize: "18px", color: "#555", marginTop: "10px" }}>
        Meet the incredible leaders driving our vision and mission forward.
      </p>

      {/* Leadership Cards */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(5, 1fr)",
          gap: "40px",
          marginTop: "40px",
        }}
      >
        {leaders.map((leader, index) => (
          <div
            key={index}
            style={{
              position: "relative",
              textAlign: "center",
            }}
          >
            {/* Image */}
            <div
              style={{
                width: "150px",
                height: "150px",
                margin: "0 auto",
                borderRadius: "50%",
                overflow: "hidden",
                position: "relative",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                cursor: "pointer",
              }}
            >
              <img
                src={leader.image}
                alt={leader.name}
                style={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  transition: "0.3s ease-in-out",
                }}
              />
              {/* Hover Icons */}
              <div
                style={{
                  position: "absolute",
                  top: "0",
                  left: "0",
                  width: "100%",
                  height: "100%",
                  backgroundColor: "rgba(0, 0, 0, 0.6)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  opacity: "0",
                  transition: "0.3s ease-in-out",
                }}
                className="hover-overlay"
              >
                {/* LinkedIn */}
                <a
                  href={leader.linkedin}
                  style={{
                    margin: "0 10px",
                    color: "#0077B5",
                    fontSize: "24px",
                  }}
                >
                  <FaLinkedin />
                </a>
                {/* Twitter */}
                <a
                  href={leader.twitter}
                  style={{
                    margin: "0 10px",
                    color: "#1DA1F2",
                    fontSize: "24px",
                  }}
                >
                  <FaTwitter />
                </a>
              </div>
            </div>

            {/* Name */}
            <p
              style={{
                marginTop: "10px",
                fontSize: "16px",
                fontWeight: "bold",
                color: "#333",
              }}
            >
              {leader.name}
            </p>
          </div>
        ))}
      </div>

      {/* CSS for Hover Effect */}
      <style>
        {`
          .hover-overlay:hover {
            opacity: 1;
          }
        `}
      </style>
    </div>
  );
};

export default Leadership;
