import React from "react";

const AboutSection = () => {
  return (
    <div
      style={{
        padding: "140px 20px",
        maxWidth: "1200px",
        margin: "0 auto",
        fontFamily: "Arial, sans-serif",
      }}
    >
      {/* About Section */}
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: "20px",
        }}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "flex-start",
            gap: "40px",
            flexWrap: "wrap",
          }}
        >
          {/* Video Card */}
          <div
            style={{
              flex: "1",
              maxWidth: "550px",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
            }}
          >
            <video
              style={{ width: "100%", display: "block" }}
              autoPlay
              loop
              muted
            >
              <source src="/videos/aboutvideo.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>

          {/* Content */}
          <div style={{ flex: "1", maxWidth: "600px" }}>
            <h2
              style={{
                fontSize: "36px",
                fontWeight: "bold",
                color: "#333",
                marginBottom: "16px",
              }}
            >
              About Us
            </h2>
            <p
              style={{
                fontSize: "16px",
                lineHeight: "1.8",
                color: "#555",
                marginBottom: "16px",
              }}
            >
              The All India Financial Borrowers Federation (AIFBF) is a
              visionary platform dedicated to empowering financial borrowers
              across India. We provide actionable insights and lawful solutions
              to help individuals and businesses thrive without hurdles. Acting
              as a catalyst for economic transformation, AIFBF bridges the gap
              between policymakers, financial institutions, and borrowers.
            </p>
            <p
              style={{
                fontSize: "16px",
                lineHeight: "1.8",
                color: "#555",
              }}
            >
              With a forward-looking and proactive approach, we aim to foster a
              better financial ecosystem for a brighter future.
            </p>
          </div>
        </div>

        {/* Cards Section */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: "40px",
            gap: "20px",
            flexWrap: "wrap",
            width: "100%",
          }}
        >
          {/* Card 1 */}
          <div
            style={{
              flex: "1",
              maxWidth: "300px",
              backgroundColor: "#f9f9f9",
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "20px",
              textAlign: "center",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
            }}
          >
            <h3
              style={{
                fontSize: "22px",
                fontWeight: "bold",
                color: "#333",
                marginBottom: "8px",
              }}
            >
              10K+ Borrowers Helped
            </h3>
            <p style={{ fontSize: "14px", color: "#555" }}>
              Trusted by thousands to achieve financial goals.
            </p>
          </div>

          {/* Card 2 */}
          <div
            style={{
              flex: "1",
              maxWidth: "300px",
              backgroundColor: "#f9f9f9",
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "20px",
              textAlign: "center",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
            }}
          >
            <h3
              style={{
                fontSize: "22px",
                fontWeight: "bold",
                color: "#333",
                marginBottom: "8px",
              }}
            >
              95% Success Rate
            </h3>
            <p style={{ fontSize: "14px", color: "#555" }}>
              A proven track record of delivering results.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AboutSection;
