import React from "react";
const AboutSection = () => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-start",
        padding: "140px",
        maxWidth: "1390px",
        margin: "0 auto",
      }}
    >
      {/* Video Card */}
      <div
        style={{
          width: "100%",
          maxWidth: "550px",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <video
          style={{ width: "100%", display: "block" }}
          autoPlay
          loop
          muted
        >
<source src="/videos/aboutvideo.mp4" type="video/mp4" />         
 Your browser does not support the video tag.
        </video>
      </div>

      {/* Content Section */}
      <div
        style={{
          marginLeft: "20px",
          flex: 1,
        }}
      >
        <h2
          style={{
            fontSize: "28px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#333",
          }}
        >
          About
        </h2>
        <p
          style={{
            fontSize: "16px",
            color: "#555",
            marginBottom: "8px",
            lineHeight: "1.6",
          }}
        >
          The All India Financial Borrowers Federation (AIFBF) is a visionary platform <></>
          dedicated to empowering financial borrowers across India. We provide actionable insights and lawful solutions to help individuals and businesses thrive without hurdles. Acting as a catalyst for economic transformation, AIFBF bridges the gap between policymakers, financial institutions, and borrowers.

With a forward-looking and proactive approach, we aim to foster a better financial ecosystem for a brighter future.
        </p>
        <p
          style={{
            fontSize: "16px",
            color: "#555",
            lineHeight: "1.6",
          }}
        >
          With a forward-looking and proactive approach, we aim to foster a better financial ecosystem for a brighter future.
        </p>
      </div>
    </div>
  );
};

export default AboutSection;
