import React from "react";

const AboutSection = () => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-start",
        padding: "20px",
        maxWidth: "1200px",
        margin: "0 auto",
      }}
    >
      {/* Video Card */}
      <div
        style={{
          width: "50%",
          maxWidth: "500px",
          borderRadius: "16px",
          overflow: "hidden",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <video
          style={{ width: "100%", display: "block" }}
          autoPlay
          loop
          muted
        >\aboutvideo.mp4
          <source src="components/aboutvideo.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>

      {/* Content Section */}
      <div
        style={{
          marginLeft: "20px",
          flex: 1,
        }}
      >
        <h2
          style={{
            fontSize: "28px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#333",
          }}
        >
          About
        </h2>
        <p
          style={{
            fontSize: "16px",
            color: "#555",
            marginBottom: "8px",
            lineHeight: "1.6",
          }}
        >
          Welcome to our platform! We are dedicated to delivering high-quality services.
        </p>
        <p
          style={{
            fontSize: "16px",
            color: "#555",
            lineHeight: "1.6",
          }}
        >
          Join us as we strive to innovate and bring exciting updates your way.
        </p>
      </div>
    </div>
  );
};

export default AboutSection;
