import React from "react";
const AboutSection = () => {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-start",
        padding: "140px",
        maxWidth: "1390px",
        margin: "0 auto",
        marginBottom: "80px",

      }}
    >
      {/* Video Card */}
      <div
        style={{
          width: "100%",
          maxWidth: "550px",
          borderRadius: "10px",
          overflow: "hidden",
          marginBottom: "20px",

          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <video
          style={{ width: "100%", display: "block" }}
          autoPlay
          loop
          muted
        >
<source src="/videos/aboutvideo.mp4" type="video/mp4" />         
 Your browser does not support the video tag.
        </video>
      </div>

      {/* Content Section */}
      <div
        style={{
          marginLeft: "80px",
          flex: 1,
        }}
      >
        <h2
          style={{
            fontSize: "50px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#333",
            marginLeft: "170px",
            marginTop: "-100px", // Adjust this value to move it upward


          }}
        >
          About Us
        </h2>
        <p
          style={{
            fontSize: "17px",
            color: "#555",
            marginBottom: "30px",
            lineHeight: "1.6",
          }}
        >
          The All India Financial Borrowers Federation (AIFBF) is a visionary platform <br></br>
          dedicated to empowering financial borrowers across India. We provide actionable <br></br>
          insights and lawful solutions to help individuals and businesses thrive without hurdles.<br></br> 
          Acting as a catalyst for economic transformation, AIFBF bridges the gap between <br></br>
          policymakers, financial institutions, and borrowers.


        </p>
        <p
          style={{
            fontSize: "16px",
            color: "#555",
            lineHeight: "1.6",
          }}
        >
          With a forward-looking and proactive approach, we aim to foster a better financial <br></br>
          ecosystem for a brighter future.
        </p>
      </div>
    </div>
  );
};

export default AboutSection;
