import React, { useState } from "react";

const Contact = () => {
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    message: "",
  });
  const regions = [
    "Bengaluru",
    "Tamil Nadu",
    "Andhra Pradesh",
    "Telangana",
    "Delhi",
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log(formState);
  };
const styles={
  regionalContainer: {
    marginTop: "50px",
    padding: "20px",
    backgroundColor: "#f1f5f9",
    borderRadius: "8px",
  },
  regionalHeading: {
    fontSize: "38px",
    fontWeight: "bold",
    color: "#ff8c00",
    textAlign:"center",
  },
  regionalDescription: {
    fontSize: "16px",
    color: "#6b7280",
    marginBottom: "20px",
  },
  regionsList: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    flexWrap: "wrap",
    marginBottom: "20px",
    fontSize: "18px",
    color: "#333",
  },
  regionContainer: {
    position: "relative", // To position the dot before the text
    paddingLeft: "20px",  // Space for the dot
  },
  regionDot: {
    position: "absolute",
    left: 0,
    top: "50%",
    transform: "translateY(-50%)",
    width: "8px",         // Size of the dot
    height: "8px",        // Size of the dot
    borderRadius: "50%",  // Make it circular
    backgroundColor: "#ff8c00", // Orange color for the dot
  },
  region: {
    color: "#ff8c00",  // Orange color for the region name
  },
  separator: {
    width: "2px",
    height: "2px",
    borderRadius: "50%",
    backgroundColor: "#3b82f6", // This can remain blue for separators
  },
  mapContainer: {
    marginTop: "20px",
  },
  findUsButton: {
    marginTop: "20px",
  },
  findUsLink: {
    padding: "10px 20px",
    backgroundColor: "#ff8c00",
    color: "white",
    textDecoration: "none",
    borderRadius: "8px",
    fontWeight: "bold",
  },
}
  return (
    <div
      id="contact"
      style={{
        padding: "6rem 0",
        backgroundColor: "white",
        fontFamily: "Arial, sans-serif",
        backgroundColor:"#DBF9DB",

      }}
    >
      {/* Regional Presence Section */}
      <div style={styles.regionalContainer}>
        <h2 style={styles.regionalHeading}>Regional Presence</h2>
        <p style={styles.regionalDescription}>
          Our strategic locations across India enable us to serve clients effectively.
        </p>
        <div style={styles.regionsList}>
          {regions.map((region, index) => (
            <React.Fragment key={region}>
              <div style={styles.regionContainer}>
                <span style={styles.regionDot}></span>
                <span style={styles.region}>{region}</span>
              </div>
              {index < regions.length - 1 && (
                <span style={styles.separator}></span>
              )}
            </React.Fragment>
          ))}
        </div>

        {/* Google Maps iframe */}
        <div style={styles.mapContainer}>
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d497699.9973874144!2d77.35074421903454!3d12.95384772557775!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bae1670c9b44e6d%3A0xf8dfc3e8517e4fe0!2sBengaluru%2C%20Karnataka!5e0!3m2!1sen!2sin!4v1645451087254!5m2!1sen!2sin"
            width="100%"
            height="300px"
            style={{ border: 0 }}
            allowFullScreen
            loading="lazy"
          ></iframe>
        </div>

        <div style={styles.findUsButton}>
          <a href="#contact" style={styles.findUsLink}>
            Find Us Near You
          </a>
        </div>
      </div>
      <div
        style={{
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "0 1rem",
        }}
      >
        <div style={{ textAlign: "center", marginBottom: "2rem" }}>
          <h2
            style={{
              fontSize: "2rem",
              fontWeight: "bold",
              color: "#FB923C",
            }}
          >
            Get In Touch
          </h2>
          <p style={{ fontSize: "1.125rem", color: "gray-600" }}>
          We're here to help with your financial advocacy needs
          </p>
        </div>

        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "2rem",
          }}
        >
          {/* Left Side: Contact Info */}
          <div style={{ flex: "1", minWidth: "300px" }}>
            <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
              <div
                style={{
                  width: "24px",
                  height: "24px",
                  color: "#F97316",
                }}
              >
                ✉️
              </div>
              <div>
                <h3
                  style={{
                    fontSize: "1rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                  }}
                >
                  Email
                </h3>
                <p style={{ fontSize: "0.875rem", color: "#666" }}>
                  info@aifbf.org
                </p>
              </div>
            </div>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: "1rem",
                marginTop: "1.5rem",
              }}
            >
              <div
                style={{
                  width: "24px",
                  height: "24px",
                  color: "#10B981",
                }}
              >
                📞
              </div>
              <div>
                <h3
                  style={{
                    fontSize: "1rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                  }}
                >
                  Phone
                </h3>
                <p style={{ fontSize: "0.875rem", color: "#666" }}>
                  +91 8792006430
                </p>
              </div>
            </div>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: "1rem",
                marginTop: "1.5rem",
              }}
            >
              <div
                style={{
                  width: "24px",
                  height: "24px",
                  color: "#F97316",
                }}
              >
                📍
              </div>
              <div>
                <h3
                  style={{
                    fontSize: "1rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                  }}
                >
                  Address
                </h3>
                <p style={{ fontSize: "0.875rem", color: "#666" }}>
                  ANDAPURA VILLAGE, ATTIBELE HOBLI,
                  <br />
                  ANEKAL TALUK,
                  <br />
                  ELECTRONIC CITY POST, Bengaluru Karnataka, 560100
                </p>
              </div>
            </div>
          </div>

          {/* Right Side: Contact Form */}
          <div
            style={{
              flex: "1",
              minWidth: "300px",
              backgroundColor: "#f9f9f9",
              padding: "2rem",
              borderRadius: "8px",
            }}
          >
            <form
              onSubmit={handleSubmit}
              style={{ display: "flex", flexDirection: "column", gap: "1rem" }}
            >
              <div>
                <label
                  htmlFor="name"
                  style={{
                    display: "block",
                    fontSize: "0.875rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                    marginBottom: "0.5rem",
                  }}
                >
                  Name
                </label>
                <input
                  type="text"
                  id="name"
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    border: "1px solid #ccc",
                    borderRadius: "4px",
                  }}
                  value={formState.name}
                  onChange={(e) =>
                    setFormState({ ...formState, name: e.target.value })
                  }
                  required
                />
              </div>
              <div>
                <label
                  htmlFor="email"
                  style={{
                    display: "block",
                    fontSize: "0.875rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                    marginBottom: "0.5rem",
                  }}
                >
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    border: "1px solid #ccc",
                    borderRadius: "4px",
                  }}
                  value={formState.email}
                  onChange={(e) =>
                    setFormState({ ...formState, email: e.target.value })
                  }
                  required
                />
              </div>
              <div>
                <label
                  htmlFor="message"
                  style={{
                    display: "block",
                    fontSize: "0.875rem",
                    fontWeight: "bold",
                    color: "#1a1a1a",
                    marginBottom: "0.5rem",
                  }}
                >
                  Message
                </label>
                <textarea
                  id="message"
                  rows={4}
                  style={{
                    width: "100%",
                    padding: "0.5rem",
                    border: "1px solid #ccc",
                    borderRadius: "4px",
                  }}
                  value={formState.message}
                  onChange={(e) =>
                    setFormState({ ...formState, message: e.target.value })
                  }
                  required
                />
              </div>
              <button
                type="submit"
                style={{
                  width: "100%",
                  backgroundColor: "#FB923C",
                  color: "white",
                  padding: "0.75rem",
                  borderRadius: "4px",
                  fontWeight: "bold",
                  border: "none",
                  cursor: "pointer",
                  transition: "background-color 0.3s",
                }}
                onMouseEnter={(e) =>
                  (e.target.style.backgroundColor = "#FB923C")
                }
                onMouseLeave={(e) =>
                  (e.target.style.backgroundColor = "#FB923C")
                }
              >
                Send Message
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  
);
};

export default Contact;
