import React, { useState } from "react";
import myVideo from '../components/homepagebgvd.mp4';
import logo from '../components/llogo.png';
const Home = () => {
  const [isWindowVisible, setIsWindowVisible] = useState(false); // State to show/hide the small window
  const containerStyle = {
    position: "relative",
    width: "100vw",
    height: "100vh",
    overflow: "hidden",
    color: "#fff",
  };

  const videoStyle = {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    objectFit: "cover",
    zIndex: -1,
  };
  const logoStyle = {
    height: "40px", // Adjust the size of the logo
    width: "auto",
  };
 

  // New overlay style to darken the background video
  const overlayStyle = {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(12, 8, 8,0.80)", // Adjust the opacity (0.5 for moderate darkening)
    zIndex: 0,
  };

  const navStyle = {
    display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  padding: "10px 20px",
  backgroundColor: "rgba(0, 0, 0, 0.6)",
  position: "fixed",
  top: 0,
  width: "100%",
  zIndex: 10,
  height: "70px",
  fontFamily: "Arial, sans-serif",
  overflow: "hidden", // Ensures no part of the navbar overflows
  boxSizing: "border-box", 
  };

  const navItemStyle = {
    color: "#fff",  // Default color (white)
    fontSize: "16px",

    fontWeight: "bold",
    textDecoration: "none",
    margin: "0 15px",
    cursor: "pointer",
    padding: "8px 12px",
    borderRadius: "5px",
    transition: "all 0.3s ease",
  };
  const navItemHoverStyle = {
  color: "yellow",  // Hover color
};
  const handleMouseOver = (e) => {
    // Apply hover styles
    Object.assign(e.target.style, navItemHoverStyle);
  };
  
  const handleMouseOut = (e) => {
    // Revert to the original style
    Object.assign(e.target.style, navItemStyle);
  };
  const contactStyle = {
    ...navItemStyle,
    backgroundColor: "yellow",
    color: "#000",
    fontWeight: "bold",
    borderRadius: "5px",
  };

  const leftContentStyle = {
    position: "absolute",
    top: "30%",
    left: "10%",
    transform: "translateY(-50%)",
    textAlign: "left",
    maxWidth: "400px",
  };

  const headingStyle = {
    fontSize: "48px",
    marginTop: "250px",
    color: "#fff",
    maxWidth: "100%",
    lineHeight: "1",
    textAlign: "left",
    whiteSpace: "nowrap",
    textOverflow: "ellipsis",
  };

  const subHeadingStyle = {
    fontSize: "18px",
    lineHeight: "1.2",
    marginBottom: "20px",
    color: "#ddd",
    maxWidth:"100%",
    textAlign:"left",
    whiteSpace: "nowrap",
  };

  const buttonContainerStyle = {
    display: "flex",
    gap: "15px",
  };

  const buttonStyle = {
    padding: "15px 25px",
    fontSize: "16px",
    fontWeight: "bold",
    color: "#fff",
    backgroundColor: "#007BFF",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  };

  const buttonStyleAlt = {
    ...buttonStyle,
    backgroundColor: "transparent",
     border: "2px solid #007BFF",
    

  };

  const arrowButtonStyle = {
    position: "absolute",
    bottom: "20px",
    left: "50%",
    transform: "translateX(-50%)",
    fontSize: "24px",
    backgroundColor: "transparent",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    animation: "bounce 2s infinite",
  };
  const smallWindowStyle = {
    position: "absolute",
    top: "40%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "black",
    padding: "20px",
    borderRadius: "10px",
    textAlign: "center",
    color: "white",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    zIndex: 30,
    width: "300px",
  };

  const smallWindowButtonContainer = {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "20px",
   
  };

  const outsideButtonStyle = {
    padding: "10px 20px",
    fontSize: "14px",
    fontWeight: "bold",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  };
  return (
    <div style={containerStyle}>
      {/* Background Video */}
      <video
        style={videoStyle}
        src={myVideo}
        autoPlay
        loop
        muted
      />

      {/* Overlay to darken the video */}
      <div style={overlayStyle}></div>

      <nav style={navStyle}>
      <div>
          <img src={logo} alt="Logo" style={logoStyle} />AIFBF
        </div>
        <div style={{ fontSize: "20px", fontWeight: "bold" }}></div>
        <div style={{ display: "flex" }}>
          {["Home", "About", "Sectors", "Leadership", "Contact"].map((item, index) => (
            <span
              key={item}
              style={item === "Contact" ? contactStyle : navItemStyle}
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              {item}
            </span>
            
          ))}
          
        </div>
      </nav>

      {/* Left Content */}
      <div style={leftContentStyle}>
        <h2 style={headingStyle}>Advocating for Financial <br />Inclusive and Borrower <br />Empowerment </h2>
        <p style={subHeadingStyle}>
          We're committed to creating a more equitable financial system where <br />everyone has access to fair lending practices and opportunities for <br />financial growth.
        </p>
        <div style={buttonContainerStyle}>
        <button style={buttonStyle} onClick={() => setIsWindowVisible(true)}>
            Get Started
          </button>
          <button style={buttonStyleAlt}>Learn More</button>
        </div>
      </div>

 {isWindowVisible && (
        <>
          <div style={smallWindowStyle}>
            <h3>Welcome to AIFBF</h3>
            <p>
              Explore our features to empower borrowers<br /> 
              and achieve financial growth.<br />
              We're excited to have you here.
            </p>
          </div>
          {/* Buttons Below the Small Window */}
          <div style={{ textAlign: "center", marginTop: "20px", position: "absolute", top: "60%", left: "50%", transform: "translateX(-50%)" }}>
            <button
              style={{ ...outsideButtonStyle, backgroundColor: "#007BFF", color: "#fff" }}
              onClick={() => alert("Sign Up process started!")}
            >
              Sign Up Now
            </button>
            <button
              style={{ ...outsideButtonStyle, backgroundColor: "#ddd", color: "#000" }}
              onClick={() => setIsWindowVisible(false)}
            >
              Maybe Later
            </button>
          </div>
        </>
 )}
      <button
        style={arrowButtonStyle}
        onClick={() => window.alert("Navigate to the next page!")}
      >
        ↓
      </button>
    </div>
  );
}

export default Home;
