import React, { useState } from "react";

const About = () => {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "flex-start",
        padding: "140px",
        maxWidth: "1390px",
      }}
    >
      {/* Video Card */}
      <div
        style={{
          width: "100%",
          maxWidth: "550px",
          borderRadius: "10px",
          overflow: "hidden",
          marginBottom: "20px",
          marginTop: "-700px",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          position: "relative",
        }}
      >
        <video style={{ width: "100%", display: "block" }} autoPlay loop muted>
          <source src="/videos/aboutvideo.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <button
          onClick={handleOpenModal}
          style={{
            position: "absolute",
            bottom: "10px",
            right: "10px",
            backgroundColor: "orange",
            color: "white",
            border: "none",
            borderRadius: "5px",
            padding: "10px 20px",
            cursor: "pointer",
            fontSize: "16px",
            fontWeight: "bold",
            boxShadow: "0 2px 4px rgba(0, 0, 0, 0.2)",
          }}
        >
          Learn More
        </button>
      </div>

      {/* Modal */}
      {showModal && (
        <div
          style={{
            position: "fixed",
            top: "0",
            left: "0",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.6)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: "1000",
          }}
        >
          <div
            style={{
              backgroundColor: "white",
              borderRadius: "10px",
              padding: "20px",
              width: "80%",
              maxWidth: "500px",
              textAlign: "center",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
            }}
          >
            <h2
              style={{
                fontSize: "24px",
                fontWeight: "bold",
                color: "orange",
                marginBottom: "20px",
              }}
            >
              More About Us
            </h2>
            <p style={{ fontSize: "16px", color: "#555", marginBottom: "20px" }}>
              Welcome to the All India Financial Borrowers Federation (AIFBF).
              Here, we strive to provide financial empowerment and support to
              all borrowers across the nation. Our goal is to bridge the gap
              between borrowers and policy institutions for a better financial
              ecosystem.
            </p>
            <button
              onClick={handleCloseModal}
              style={{
                backgroundColor: "red",
                color: "white",
                border: "none",
                borderRadius: "5px",
                padding: "10px 20px",
                cursor: "pointer",
                fontSize: "16px",
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Rest of your content here */}
      <div
        style={{
          marginLeft: "80px",
          flex: 1,
        }}
      >
        <h2
          style={{
            fontSize: "50px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "orange",
            marginLeft: "170px",
            marginTop: "-50px",
          }}
        >
          About Us
        </h2>
        <p
          style={{
            fontSize: "18px",
            color: "#555",
            marginBottom: "30px",
            lineHeight: "1.4",
          }}
        >
          The
          <span style={{ color: "green" }}>
            {" "}
            All India Financial Borrowers Federation (AIFBF)
          </span>{" "}
          is a visionary platform dedicated to empowering financial borrowers
          across India. We provide actionable insights and lawful solutions to
          help individuals and businesses thrive without hurdles. Acting as a
          catalyst for economic transformation, AIFBF bridges the gap between
          policymakers, financial institutions, and borrowers.
        </p>
      </div>
    </div>
  );
};

export default About;
