import React, { useState } from 'react';

const Sectors = () => {
  const [modalOpen, setModalOpen] = useState(null);
  const [contentWindowOpen, setContentWindowOpen] = useState(false);
  const [showMore, setShowMore] = useState(false);
  const sectorsData = [
    { title: 'MSME / SME', iconClass: 'text-primary' },
    { title: 'Skill and Women Empowerment', iconClass: 'text-secondary' },
    { title: 'Clean Energy and Climate Change', iconClass: 'text-primary' },
    { title: 'Banking and Capital Markets', iconClass: 'text-secondary' },
    // Additional sectors to show when "View More" is clicked
    { title: 'Healthcare & Medicine', iconClass: 'text-primary' },
    { title: 'Education & Learning', iconClass: 'text-secondary' },
    { title: 'Technology & Innovation', iconClass: 'text-primary' },
    { title: 'Tourism & Hospitality', iconClass: 'text-secondary' },
    { title: 'Agriculture & Food', iconClass: 'text-primary' },
    { title: 'Retail & E-commerce', iconClass: 'text-secondary' },
    { title: 'Finance & Investment', iconClass: 'text-primary' },
    { title: 'Real Estate & Infrastructure', iconClass: 'text-secondary' },
  ];
  
  // Handle opening of the modal for each objective
  const handleModalOpen = (index) => {
    setModalOpen(index);
  };

  // Handle closing the modal
  const handleModalClose = () => {
    setModalOpen(null);
  };

  const handleContentWindowOpen = () => {
    setContentWindowOpen(true);
  };

  // Handle closing the content window
  const handleContentWindowClose = () => {
    setContentWindowOpen(false);
  };
  return (
    <div
      id="sectors"
      style={{
        paddingTop: '5rem',
        paddingBottom: '5rem',
        backgroundImage: 'linear-gradient(to bottom left, #FFF4E6, #FFFFFF, #E4F9D9)',
      }}
    >
      <div style={{ maxWidth: '80rem', margin: '0 auto', paddingLeft: '1rem', paddingRight: '1rem' }}>
        <div style={{ textAlign: 'center', marginBottom: '4rem' }}>
          <h2
            style={{
              fontSize: '1.875rem',
              fontWeight: '700',
              color: '#1F2937',
            }}
          >
            <span
              style={{
                color: '#FB923C',
                fontSize: '2.5rem',
                fontWeight: '700',
              }}
            >
              Our Objectives
            </span>
          </h2>
          <p
            style={{
              marginTop: '1rem',
              fontSize: '1.25rem',
              color: '#4B5563',
            }}
          >
            The objectives of AIFBF are to promote economic growth, enhance financial systems, and equip stakeholders
            with the necessary skills to drive sustainable development and contribute to India's GDP.
          </p>
        </div>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '2rem' }}>
          {/* Growth */}
          <div
            style={{
              opacity: 1,
              transform: 'none',
            }}
          >
            <div
              style={{
                padding: '1.5rem',
                backgroundColor: 'rgba(255, 255, 255, 0.5)',
                backdropFilter: 'blur(10px)',
                borderRadius: '0.5rem',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                position: 'relative',
                cursor: 'pointer',
                transition: 'all 0.3s ease-in-out',
              }}
            >
              <div
                style={{
                  marginBottom: '1rem',
                  transition: 'transform 0.3s ease',
                }}
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  style={{
                    height: '3rem',
                    width: '3rem',
