import React from "react";

const AboutCard = () => {
  return (
    <div className="flex flex-col md:flex-row items-center md:items-start bg-white shadow-lg rounded-2xl overflow-hidden p-4 md:p-6">
      {/* Video Section */}
      <div className="w-full md:w-1/2">
        <video
          className="w-full rounded-lg"
          controls
          poster="https://via.placeholder.com/300"
        >
          <source src="https://www.w3schools.com/html/mov_bbb.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>

      {/* Content Section */}
      <div className="w-full md:w-1/2 mt-4 md:mt-0 md:pl-6">
        <h2 className="text-xl md:text-2xl font-bold text-gray-800">About</h2>
        <p className="text-gray-600 mt-2 text-sm md:text-base leading-relaxed">
          Welcome to our platform! We aim to provide high-quality services and experiences tailored 
          just for you. Our team is passionate about delivering excellence in everything we do. 
          Stay tuned as we bring more exciting updates your way.
        </p>
      </div>
    </div>
  );
};

export default About;
