import React from "react";

const AboutSection = () => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column", // Stack content vertically
        alignItems: "center",
        padding: "140px",
        maxWidth: "1390px",
        margin: "0 auto",
      }}
    >
      {/* Main Section */}
      <div
        style={{
          display: "flex",
          alignItems: "flex-start",
          justifyContent: "flex-start",
          width: "100%",
        }}
      >
        {/* Video Card */}
        <div
          style={{
            width: "100%",
            maxWidth: "550px",
            borderRadius: "10px",
            overflow: "hidden",
            marginBottom: "20px",
            marginTop: "40px", // Adjust this value to move the video down
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          }}
        >
          <video
            style={{ width: "100%", display: "block" }}
            autoPlay
            loop
            muted
          >
            <source src="/videos/aboutvideo.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>

        {/* Content Section */}
        <div
          style={{
            marginLeft: "80px",
            flex: 1,
          }}
        >
          <h2
            style={{
              fontSize: "50px",
              fontWeight: "bold",
              marginBottom: "10px",
              color: "#333",
              marginLeft: "170px",
              marginTop: "-100px", // Adjust this value to move it upward
            }}
          >
            About Us
          </h2>
          <p
            style={{
              fontSize: "18px",
              color: "#555",
              marginBottom: "30px",
              lineHeight: "1.6",
            }}
          >
            The All India Financial Borrowers Federation (AIFBF) is a visionary
            <br />
            platform dedicated to empowering financial borrowers across India.
            We
            <br />
            provide actionable insights and lawful solutions to help individuals
            and
            <br />
            businesses thrive without hurdles. Acting as a catalyst for economic
            <br />
            transformation, AIFBF bridges the gap between policymakers,
            financial
            <br />
            institutions, and borrowers.
          </p>
          <p
            style={{
              fontSize: "18px",
              color: "#555",
              lineHeight: "1.6",
            }}
          >
            With a forward-looking and proactive approach, we aim to foster a
            better
            <br />
            financial ecosystem for a brighter future.
          </p>
        </div>
      </div>

      {/* Cards Section */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          marginTop: "40px",
          width: "100%",
        }}
      >
        {/* Card 1 */}
        <div
          style={{
            backgroundColor: "#f9f9f9",
            border: "1px solid #ddd",
            borderRadius: "12px",
            padding: "20px",
            width: "45%",
            textAlign: "center",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h3
            style={{
              fontSize: "22px",
              fontWeight: "bold",
              color: "#333",
              marginBottom: "10px",
            }}
          >
            10K+ Borrowers Helped
          </h3>
          <p style={{ fontSize: "14px", color: "#555" }}>
            Trusted by thousands to achieve financial goals.
          </p>
        </div>

        {/* Card 2 */}
        <div
          style={{
            backgroundColor: "#f9f9f9",
            border: "1px solid #ddd",
            borderRadius: "12px",
            padding: "20px",
            width: "45%",
            textAlign: "center",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h3
            style={{
              fontSize: "22px",
              fontWeight: "bold",
              color: "#333",
              marginBottom: "10px",
            }}
          >
            95% Success Rate
          </h3>
          <p style={{ fontSize: "14px", color: "#555" }}>
            A proven track record of delivering results.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutSection;
