import React, { useState } from 'react';

const Sectors = () => {
  const [showMore, setShowMore] = useState(false);

  // Example sector data with different icons
  const sectorsData = [
    { title: 'MSME / SME', icon: 'briefcase', iconClass: 'text-primary' },
    { title: 'Skill and Women Empowerment', icon: 'users', iconClass: 'text-secondary' },
    { title: 'Clean Energy and Climate Change', icon: 'bolt', iconClass: 'text-primary' },
    { title: 'Banking and Capital Markets', icon: 'banknote', iconClass: 'text-secondary' },
    // Additional sectors to show when "View More" is clicked
    { title: 'Healthcare & Medicine', icon: 'heart', iconClass: 'text-primary' },
    { title: 'Education & Learning', icon: 'book', iconClass: 'text-secondary' },
    { title: 'Technology & Innovation', icon: 'laptop', iconClass: 'text-primary' },
    { title: 'Tourism & Hospitality', icon: 'airplane', iconClass: 'text-secondary' },
    { title: 'Agriculture & Food', icon: 'leaf', iconClass: 'text-primary' },
    { title: 'Retail & E-commerce', icon: 'shopping-cart', iconClass: 'text-secondary' },
    { title: 'Finance & Investment', icon: 'dollar-sign', iconClass: 'text-primary' },
    { title: 'Real Estate & Infrastructure', icon: 'home', iconClass: 'text-secondary' },
  ];

  // Function to handle the "View More" button click
  const handleViewMore = () => {
    setShowMore(!showMore);
  };

  // Function to render the SVG based on the icon name
  const renderIcon = (iconName) => {
    switch (iconName) {
      case 'briefcase':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <rect width="20" height="14" x="2" y="7" rx="2" ry="2"></rect>
            <path d="M16 21V5a2 2 0 0 0-2-2h-4a2 2 0 0 0-2 2v16"></path>
          </svg>
        );
      case 'users':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2"></path>
            <circle cx="9" cy="7" r="4"></circle>
            <path d="M22 21v-2a4 4 0 0 0-3-3.87"></path>
            <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
          </svg>
        );
      case 'bolt':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z"></path>
            <circle cx="12" cy="12" r="4"></circle>
          </svg>
        );
      case 'banknote':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <rect width="20" height="12" x="2" y="6" rx="2"></rect>
            <circle cx="12" cy="12" r="2"></circle>
            <path d="M6 12h.01M18 12h.01"></path>
          </svg>
        );
      case 'heart':
        return (
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M12 21c-6-4.5-10-7.5-10-12A6 6 0 0 1 12 3a6 6 0 0 1 10 6c0 4.5-4 7.5-10 12z"></path>
          </svg>
        );
        case 'book':
          return (
            <svg xmlns="http://www.w3.org/2000/svg" width="300" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M12 21c-6-4.5-10-7.5-10-12A6 6 0 0 1 12 3a6 6 0 0 1 10 6c0 4.5-4 7.5-10 12z"></path>
            </svg>
          );
      // Add more cases for other icons as needed
      default:
        return null;
    }
  };

  return (
    <div id="sectors" className="sectors-section">
      <style>
        {`
          .sectors-section {
            padding: 80px 110px;
            background: linear-gradient(to center, #fff4e5, #fff, #e0f7fa);
          }

          .section-title {
            text-align: center;
            margin-bottom: 64px;
          }

          .highlighted-title {
            font-size: 2.5rem;
            color: #FF7043;
            font-weight: bold;
          }

          .section-description {
            margin-top: 16px;
            font-size: 1.25rem;
            color: #4B5563;
          }

          .sectors-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr); /* Ensure 4 cards per row */
            gap: 42px;
            justify-items: center; /* Center the items within the grid */
          }

          .sector-card {
            width: 100%;
            max-width: 250px; /* Ensure cards don't grow too large */
          }

          .sector-card-inner {
            padding: 24px;
            background-color: rgba(255, 255, 255, 0.5);
            backdrop-filter: blur(10px);
            border-radius: 12px;
            transition: all 0.3s ease;
            cursor: pointer;
          }

          .sector-card-inner:hover {
            box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.2);
            transform: translateY(-8px);
          }

          .icon-wrapper {
            margin-bottom: 16px;
            transition: transform 0.3s ease;
          }

          .icon-wrapper:hover {
            transform: scale(1.1);
          }

          .sector-title {
            font-size: 1.25rem;
            font-weight: 600;
            color: #1F2937;
            margin-bottom: 8px;
          }

          .view-more-button {
            padding: 12px 24px;
            background-color: #FF5722;
            color: white;
            border-radius: 8px;
            transition: background-color 0.3s ease;
          }

          .view-more-button:hover {
            background-color: #e64a19;
          }

          /* Alternate color styles for the icons */
          .text-primary {
            color: #FF7043; /* Orange */
          }

          .text-secondary {
            color: #4CAF50; /* Green */
          }
        `}
      </style>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="section-title">
            <span className="highlighted-title">Our Sectors</span>
          </h2>
          <p className="section-description">
            AIFBF supports various sectors that significantly contribute to India's GDP
          </p>
        </div>

        <div className="sectors-grid">
          {sectorsData.slice(0, showMore ? sectorsData.length : 4).map((sector, index) => (
            <div className="sector-card" key={index}>
              <div className="sector-card-inner">
                <div className="icon-wrapper">
                  {renderIcon(sector.icon)}
                </div>
                <h3 className="sector-title">{sector.title}</h3>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-6">
          <button onClick={handleViewMore} className="view-more-button">
            {showMore ? 'View Less' : 'View More Sectors'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sectors;
