import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const navStyle = {
        display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "10px 20px",
      backgroundColor: "rgba(0, 0, 0, 0.6)",
      position: "fixed",
      top: 0,
      width: "100%",
      zIndex: 10,
      height: "70px",
      fontFamily: "Arial, sans-serif",
      overflow: "hidden", // Ensures no part of the navbar overflows
      boxSizing: "border-box", 
      };
    
      const navItemStyle = {
        color: "#fff",  // Default color (white)
        fontSize: "16px",
        fontWeight: "bold",
        textDecoration: "none",
        margin: "0 15px",
        cursor: "pointer",
        padding: "8px 12px",
        borderRadius: "5px",
        transition: "all 0.3s ease",
      };
      const navItemHoverStyle = {
      color: "yellow",  // Hover color
    };
      const handleMouseOver = (e) => {
        // Apply hover styles
        Object.assign(e.target.style, navItemHoverStyle);
      };
      
      const handleMouseOut = (e) => {
        // Revert to the original style
        Object.assign(e.target.style, navItemStyle);
      };
      const contactStyle = {
        ...navItemStyle,
        backgroundColor: "yellow",
        color: "#000",
        fontWeight: "bold",
        borderRadius: "5px",
      };
 

};

export default Navbar;
